mapper.update:
UPDATE react_board
      SET b_title = #{b_title}
              ,email = #{email}
              ,b_content = #{b_content}

              ,b_file = #{b_file}

WHERE b_no = #{b_no}

UPDATE react_board
SET b_hit = b_hit + 1
WHERE b_no =#{b_no}

UPDATE react_board_comment
       SET bc_comment = #{bc_comment}
	          ,bc_date = to_char(sysdate, 'YYYY-MM-DD')
WHERE bc_no = #{bc_no}
