mapper.update:
update react_board
  set b_title = #{b_title}
      ,email = #{email}
      ,b_content = #{b_content}
      ,b_file = #{b_file}
  where b_no = #{b_no}

	UPDATE react_board_comment
      SET bc_comment = #{bc_comment}
	        ,bc_date = to_char(sysdate, 'YYYY-MM-DD')
WHERE bc_no = #{bc_no}
