import React, { useState } from 'react'
import Header from '../include1211/Header';
import Footer from '../include1211/Footer';
import styled from 'styled-components';
import { loginGoogle, onAuthChange } from '../../service/authLogic1212';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';

const LoginDiv = styled.div`
    width: 30em;
    text-align: center;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: transparent;    
`
const Section = styled.div`
    background-color: white;    
`
const ListUl = styled.ul`
    width: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    list-style: none;
    padding: 0.5rem; 
`
const ItemLi = styled.li`
    margin-bottom: 0.5em;
`
const BtnLogin = styled.button`
    width: 80%;
    height: 2.5em;
    font-size: 1.2rem;
    border-radius: 1.2rem;
    background-color: transparent;
    cursor: pointer;
    border: 0.2rem solid lightgray;
    outline: 0;
    &:hover{
      background-color: #e8e7e6;//#FEFCF3 , #F5EBE0
    }
`
const Login = ({ authLogic }) => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState();
  const goToMaker = userId => {
    navigate({
      pathname: '/marker',
      state: { id: userId },
    })
  }

  const onLogin = async () => {
    //authLogic에 있는 로그인 함수 호출함
    try {
      const user = await loginGoogle(authLogic.auth, authLogic.googleProvider);
      //구글 인증 후 응답으로 받은 프로필 정보에서 userId값을 꺼낸다
      setUserId(user.uid);
      //window.localStorage.setItem("userId", user.uid);
      if (user.uid) {
        goToMaker(user.uid);
      }
    } catch (error) {
      console.log('로그인 오류입니다');
    }
  }
  return (
    <LoginDiv>
      <Header />
      <Section>
        <h1>Login</h1>
        <ListUl>
          <ItemLi>
            <BtnLogin onClick={onLogin}>Google</BtnLogin>
          </ItemLi>
          <ItemLi>
            <BtnLogin onClick={onLogin}>Github</BtnLogin>
          </ItemLi>
        </ListUl>
      </Section>
      <Footer />
    </LoginDiv>
  );
}

export default Login
